import { useState } from 'react';
import Header from './components/Layout/Header';
import Meals from './components/Meals/Meals';
import Cart from './components/Cart/Cart';
import CartProvider from './store/CartProvider';
import BillForm from './components/Bill/BillForm';
import { uiActions } from './store/uiSlice';
import {useSelector,useDispatch} from 'react-redux'

function App() {
  const [cartIsShown, setCartIsShown] = useState(false);
  const [billForm,setOpetBillForm]=useState(false);
  const dispatch=useDispatch();
  const displayCart=useSelector(state=>state.ui.cartIsVisible)

  const showCartHandler = () => {
    setCartIsShown(true)
  };

  const hideCartHandler = () => {
    setCartIsShown(false);
  };

  const onOpenBillForm=()=>{
    setOpetBillForm(true);
  }
  const onCloseBillForm=()=>{
    setOpetBillForm(false);
  }
  const onCreateBill=()=>{
    dispatch(uiActions.showBillButton())
    setOpetBillForm(false);
  }

  return (
    <CartProvider>
      {cartIsShown && <Cart onClose={showCartHandler} />}
      <Header onShowCart={showCartHandler} onShowCreateBill={onOpenBillForm}/>
      <main>
        <Meals />
        {billForm && <BillForm onClose={onCloseBillForm} onCreate={onCreateBill}/>}
      </main>
    </CartProvider>
  );
}

export default App;
