import Card from "../UI/Card";
import BillItem from "../Bill/BillItem";
import classes from "./BillsList.module.css";
import { useState, useEffect, useCallback } from "react";
import { useSelector, useDispatch } from "react-redux";
import React from "react";
import { uiActions } from "../../store/uiSlice";
import BillDetailModal from "./BillDetailModal";
import BillById from "./BillById";

const BillsList = () => {
  const [bills, setBills] = useState([]);
  const billDetail = useSelector((state) => state.ui.billDetailIsVisible);
  const [bp, setbp] = useState({});
  const [ps, setps] = useState([]);
  const dispatch = useDispatch();
  const [filtered, setFiltered] = useState(false);
  const byIdForm = useSelector((state) => state.ui.BillById);
  const [byIdBill, setByIdBill] = useState();

  const fetchBills = useCallback(async () => {
    const response = await fetch("https://localhost:7269/api/Bill/GetAllBills");

    if (!response.ok) {
      throw new Error("Something went wrong!");
    }

    const responseData = await response.json();

    const loadedBills = [];

    for (const key in responseData) {
      loadedBills.push({
        id: responseData[key].bill_number,
        totalCost: responseData[key].total_cost,
        creditCard: responseData[key].credit_card,
      });
    }

    setBills(loadedBills);
  }, []);

  const onViewDetailModal = (bn) => {
    GetBillProducts(bn);
    dispatch(uiActions.showBillDetail());
  };

  async function GetBillProducts(billNumber) {
    fetch(`https://localhost:7269/api/Bill/GetBillById${billNumber}`).then(
      (response) => {
        response.json().then((json) => {
          setbp(json);
          setps(json.bill_Products);
        });
      }
    );
  }

  const onSearchByIdForm = () => {
    dispatch(uiActions.showBillByIdForm());

  };

  // let billsList = bills.map((bill) => (
  //       <BillItem
  //         key={bill.id}
  //         id={bill.id}
  //         totalCost={bill.totalCost}
  //         creditCard={bill.creditCard}
  //         onViewDetail={onViewDetailModal}
  //       />
  //     ));
  
  let billsList
  let findedBill;
  let bild;
  const setReturn=()=>{
   billsList = bills.map((bill) => (
          <BillItem
            key={bill.id}
            id={bill.id}
            totalCost={bill.totalCost}
            creditCard={bill.creditCard}
            onViewDetail={onViewDetailModal}
          />
        ));
    findedBill=findedBill = bills.find((bill) => bill.id === byIdBill);
    bild = 
    <BillItem
      id={findedBill.id}
      totalCost={findedBill.totalCost}
      creditCard={findedBill.creditCard}
      onViewDetail={onViewDetailModal}
    />
  }

  const onFilterHandler = (billNum) => {
    setByIdBill(billNum);
    // findedBill = bills.find((bill) => bill.id === byIdBill);
    // console.log(findedBill);
    // console.log(byIdBill);
    // findedBill = bills.find((bill) => bill.id === billNum);
    // billsList = (
    //   <BillItem
    //     id={findedBill.id}
    //     totalCost={findedBill.totalCost}
    //     creditCard={findedBill.creditCard}
    //     onViewDetail={onViewDetailModal}
    //   />
    // );
    setFiltered(true);
    // console.log(billsList)
  };
  const onCloseHandler = () => {
    dispatch(uiActions.hideBillByIdForm());
  };

  useEffect(() => {
    fetchBills();
    console.log(filtered)
    console.log(billsList)
    console.log(byIdBill)
    setReturn();
  }, [fetchBills,filtered]);

  return (
    <section className={classes.bills}>
      {byIdForm && (
        <BillById onClose={onCloseHandler} onFilter={onFilterHandler} />
      )}
      {billDetail && (
        <BillDetailModal
          bn={bp.bill_number}
          tc={bp.total_cost}
          cc={bp.credit_card}
          products={ps}
        />
      )}
      {!billDetail && (
        <Card>
          <button className={classes.btn} onClick={onSearchByIdForm}>
            Get bill by id
          </button>
          {!filtered && <ul>{billsList}</ul>}
      
          {filtered && (
            <ul>
               <BillItem
                id={findedBill.id}
                totalCost={findedBill.totalCost}
                creditCard={findedBill.creditCard}
                onViewDetail={onViewDetailModal}
               /> 
            </ul>
          )}
        </Card>
      )}
    </section>
  );
};

export default BillsList;
