import { Fragment ,useContext} from 'react';
import {useSelector} from 'react-redux'
import HeaderCartButton from './HeaderCartButton';
import classes from './Header.module.css';
import { Link } from 'react-router-dom';
import AuthContext from '../../store/auth-context';
import { useHistory } from 'react-router-dom';

const Header = (props) => {

  const authCtx = useContext(AuthContext);
  const isLoggedIn = authCtx.isLoggedIn;
  const billsList=useSelector(state=>state.ui.BillsListPage)
  const billNum=useSelector(state=>state.bill.billNumber)
  const history=useHistory();

  const logoutHandler=()=>{
    authCtx.logout();
    history.replace('/auth')
  }
  console.log(isLoggedIn)
  return (
    <Fragment>
      <header className={classes.header}>

        {isLoggedIn && <Link style={{textDecoration:'none' ,color:'white'}} to='/homepage'><h1>HOMEPAGE</h1></Link>}
        {isLoggedIn && !billsList && <Link style={{textDecoration:'none'}} to='/billslist'><button className={classes.btn}>Bills List</button></Link>}
        {isLoggedIn && !billsList && <button className={classes.btn} onClick={props.onShowProductForm}>Add new Product</button>}
        {isLoggedIn && (billNum === '') && !billsList && <button className={classes.btn} onClick={props.onShowCreateBill}>Create Bill</button>} 
        {isLoggedIn && (billNum !== '') && !billsList && <HeaderCartButton onClick={props.onShowCart}/>}
        {isLoggedIn && !billsList && <button className={classes.btn} onClick={props.onShowCurrExchForm}>Currency Exchange</button>}
        {!isLoggedIn && <button className={classes.btn} onClick={logoutHandler}>Logout</button>}
        {!isLoggedIn && <h1>Welcome</h1>}
      </header>
    </Fragment>
  );
};
export default Header;
