import React, {  useState }  from "react";
import classes from "./BillItem.module.css";
import {useDispatch,useSelector} from 'react-redux'
import { uiActions } from "../../store/uiSlice";
import BillDetailModal from './BillDetailModal'
import BillDetailTest from "./BillDetailTest";
import BillDetail from "./BillDetail";

const BillItem = (props) => {
  const dispatch=useDispatch();
  const billDetail=useSelector(state=>state.ui.billDetailIsVisible)
  const [billproducts,setbillproducts]=useState([]);
  const [bp,setBP]=useState({});


  async function deleteBill(bn) {
    
    const response = await fetch(`https://localhost:7269/api/Bill/delete/${bn}`, {
      method: 'DELETE',
      
    });
    const data = await response.json();
    console.log(data);
    
  }

  const onDeleteBill=()=>{
    if( window.confirm('Are you sure you want to delete that Bill??')){
      deleteBill(props.id);
    }
    else{
      return;
    }
  }
  
    async function GetBillProducts(billNumber) {

      const response = await fetch(`https://localhost:7269/api/Bill/GetBillById${billNumber}`);
      if (!response.ok) {
          throw new Error('Something went wrong!');
        }
      
      const data = await response.json();
      console.log(data);
      setbillproducts(data.bill_Products);
      setBP(data);
      console.log(data.bill_Products);
      props.onOpenDetails(props.id,props.totalCost,props.creditCard,data.bill_Products)

    }
  
    
    const onViewDetails=()=>{
      dispatch(uiActions.showBillDetail())
      GetBillProducts(props.id);
      console.log(billDetail)
      
    }

  return (
    <div>
     <div>
   {!billDetail && <li className={classes.bill}>
      <div>
        <div className={classes.info}>
          <p>Bill number: {props.id}</p>
        </div>
        <div className={classes.info}>
          <p>Total cost : {props.totalCost}</p>
        </div>
        <div className={classes.info}>
          <p>Credit card: {props.creditCard}</p>
        </div>
        <div className={classes.price}></div>
      </div>
      <div className={classes.buttons}>
        <button className={classes.delete} onClick={onDeleteBill}>Delete bill</button>
        <button className={classes.delete} onClick={onViewDetails}>View details</button>
      </div>
    </li>}
    </div>
    <div>
      {/* {billDetail && <BillDetail bn={props.id} tc={props.totalCost} cc={props.creditCard} billproducts={billproducts}/>} */}
    {/* {billDetail && <BillDetailModal bn={props.id} tc={props.totalCost} cc={props.creditCard} products={billproducts} />} */}
    {/* {billDetail && <BillDetailTest bn={props.id} tc={props.totalCost} cc={props.creditCard} products={billproducts} />} */}

    </div>  
    </div>
  );
}
export default BillItem;
