import React, { useRef } from "react";
import Modal from "../UI/CartModal";
import classes from '../Bill/BillForm.module.css';
import {useDispatch} from 'react-redux'
import { billActions } from "../../store/billSlice";

const ProductForm=(props) =>{
  const dispatch=useDispatch();
  const product=useRef();

  async function AddProduct(billNumber) {
    const bill=JSON.stringify({bill_number:billNumber,total_cost: 0,credit_card:null,bill_Products:[]});
    const response = await fetch('https://localhost:7269/api/Bill/CreateNewBill', {
      method: 'POST',
      body: bill,
      headers: {
        'Content-Type': 'application/json'
      }
    });
    const data = await response.json();
    console.log(data);
  }
  
  const onSubmitHandler=(event)=>{
    event.preventDefault();
    const enteredBill=billInputRef.current.value;
    dispatch(billActions.createBill(enteredBill));
    props.onCreate();
    AddProduct(enteredBill)
    
  }

  return (
    <Modal>
      <form onSubmit={onSubmitHandler}>
        <label htmlFor="productId">Product id</label>
        <input id="productId" type="text" ref={IdInputRef}></input>
        <label htmlFor="name">Product name</label>
        <input id="name" type="text" ref={NameInputRef}></input>
        <label htmlFor="cost">Product cost</label>
        <input id="cost" type="text" ref={CostInputRef}></input>
      <div className={classes.actions}>
        <button className={classes['button--alt']} onClick={props.onClose}>
          Close
        </button>
        <button type="submit" className={classes.button}>Create</button>
      </div>
      </form>
    </Modal>
  );
}

export default BillForm;
