import React, { useEffect, useState } from 'react'

function BillProducts(props) {
  const [products,setProducts]=useState([]);
  

  const fetchProducts = async () => {
    const response = await fetch("https://localhost:7269/api/Product");

    if (!response.ok) {
      throw new Error("Something went wrong!");
    }

    const responseData = await response.json();

    const loadedProducts = [];

    for (const key in responseData) {
      loadedProducts.push({
        id:responseData[key].product_id,
        name: responseData[key].name,
      });
    }

    setProducts(loadedProducts);
  };
  useEffect(()=>{
    fetchProducts();
    const findedProduct = products.find((p) => p.product_id === props.productId);
  },[fetchProducts])

  return (
    <li>
        <h2>Product name: {findedProduct.name}</h2>
        <p>Product Id: {props.productId}</p>
        <p>Product quantity: {props.productQuantity}</p>
        <p>Product cost: {props.productCost}</p>
    </li>
  )
}

export default BillProducts
