import Card from '../UI/Card'
import BillItem from '../Bill/BillItem';
import classes from './BillsList.module.css';
import {useState,useEffect, useCallback} from 'react';
import {useSelector,useDispatch} from 'react-redux'
  import React from 'react';
import { uiActions } from '../../store/uiSlice';
import BillDetailModal from './BillDetailModal';


const BillsList = () => {
  const[bills,setBills]=useState([]);
  const billDetail=useSelector(state=>state.ui.billDetailIsVisible)
  const [bp,setbp]=useState({})
  const [ps,setps]=useState([]);
  const dispatch=useDispatch()

  const fetchBills = useCallback(async () => {
    const response = await fetch(
      'https://localhost:7269/api/Bill/GetAllBills'
    );

    if (!response.ok) {
      throw new Error('Something went wrong!');
    }

    const responseData = await response.json();

    const loadedBills = [];


    for (const key in responseData) {
      loadedBills.push({
        id: responseData[key].bill_number,
        totalCost: responseData[key].total_cost,
        creditCard: responseData[key].credit_card,
      });
    }

    setBills(loadedBills);
  },[]);


  useEffect(() => {
    fetchBills();
    console.log(billDetail)
  }, [fetchBills,billDetail]);

const onhide=()=>{
  dispatch(uiActions.hideBillDetail())
  console.log(billDetail)
}

const onViewDetailModal=(bn)=>{
  GetBillProducts(bn);
  dispatch(uiActions.showBillDetail())
  console.log(bp)
  console.log(ps)
}

async function GetBillProducts(billNumber) {

  const response = await fetch(`https://localhost:7269/api/Bill/GetBillById${billNumber}`);
  if (!response.ok) {
      throw new Error('Something went wrong!');
    }
  
  const data = await response.json();
  console.log(data);
  setbp(data);
  console.log(data.bill_Products)
  setps(data.bill_Products)
  console.log(typeof ps)
  console.log(bp)
  // setbillproducts(data.bill_Products);
  // props.onViewDetail(data.bill_number,data.total_cost,data.credit_card,data.bill_Products)
  // onViewDetailModal(data.bill_number,data.total_cost,data.credit_card,data.bill_Products);
  
}

  const billsList = bills.map((bill) => (
    <BillItem
      key={bill.id}
      id={bill.id}
      totalCost={bill.totalCost}
      creditCard={bill.creditCard}
      onViewDetail={onViewDetailModal}
    />
  ));

  return (
    <section className={classes.bills}>
    {!billDetail && <Card>
        <button className={classes.btn} onClick={onhide}>Get bill by id</button>
        <ul>{billsList}</ul>
      </Card>}
    {billDetail && <BillDetailModal bn={bp.bill_number} tc={bp.total_cost} cc={bp.credit_card} products={bp.bill_Products}/>}
    </section>
  );
};

export default BillsList;
