import { useState, useRef, useContext } from 'react';
import { useHistory } from 'react-router-dom';
import AuthContext from '../../store/auth-context';
import classes from './AuthForm.module.css';
import { billActions } from '../../store/billSlice';
import {useDispatch} from 'react-redux';
import { uiActions } from '../../store/uiSlice';
import useInput from '../../hooks/use-input';

const isNotEmpty=(value)=>value.trim() !== '';

const LoginForm = () => {
    //
    //
    const {
        value: usernameValue,
        isValid: usernameIsValid,
        hasError: usernameHasError,
        valueChangeHandler: usernameChangeHandler,
        inputBlurHandler: usernameBlurHandler,
        reset: resetUsername,
      } = useInput(isNotEmpty);
      const {
        value: passwordValue,
        isValid: passwordIsValid,
        hasError: passwordHasError,
        valueChangeHandler: passwordChangeHandler,
        inputBlurHandler: passwordBlurHandler,
        reset: resetPassword,
      } = useInput(isNotEmpty);

      let formIsValid=false;
      if(usernameIsValid && passwordIsValid){
      formIsValid=true;
    }
//
//   
  const history = useHistory();
  const usernameInputRef = useRef();
  const passwordInputRef = useRef();
  const dispatch=useDispatch()
  
  const authCtx = useContext(AuthContext);

  async function GetUsersId(username){
    const response = await fetch(`https://localhost:7269/api/User/GetUsersId/${username}`);
    const data = await response.json();
    console.log(response)
    console.log(data);
    dispatch(billActions.setUserId(data))  
  }

  async function Login(userCredsObj) {
    //setNotificationObj({component:'ProductForm',status:'pending',title:'Sending',message:'Sending...'})
    const credentials=JSON.stringify(userCredsObj);
    console.log(credentials)
    const response = await fetch('https://localhost:7269/api/User/login', {
      method: 'POST',
      body: credentials,
      headers: {
        'Content-Type': 'application/json',
        'Accept':'application/json'
      }
    });
    const data=await response.json()
    if (!response.ok) {
      throw new Error('error');
    }
    const expirationTime = new Date(
            new Date().setHours(new Date().getHours() + 8)
          )
    authCtx.login(data, expirationTime.toISOString());
    GetUsersId(userCredsObj.username);



    const getUserResponse = await fetch('https://localhost:7269/api/User/GetUserByCredentials', {
      method: 'POST',
      body: credentials,
      headers: {
        'Content-Type': 'application/json'
      }
    });
    const getUserData = await getUserResponse.json();

    if(getUserData.role === 'admin'){
      history.replace('/admin')
      dispatch(uiActions.isOnAdminsPage())
    }
    else if(getUserData.role === 'user'){
      history.replace('/homepage')
    }                                    
    //history.replace('/homepage');
    
   // dispatch(productActions.addProduct(productObj))
   // setNotificationObj({component:'ProductForm',status:'success',title:'Success!',message:'Successfuly created product'})
  }

  const submitHandler = (event) => {
    event.preventDefault();
    if (!formIsValid)  {
        return;
      }
    // const enteredUsername = usernameInputRef.current.value;
    // const enteredPassword = passwordInputRef.current.value;
    
    // optional: Add validation
 
    // setIsLoading(true);
      const credentials={username:usernameValue,password:passwordValue}
      Login(credentials).catch(error=>{
        console.log('eeroror');
      });
    
    resetUsername()
    resetPassword()
}

  const clearNotificationHandler=()=>{
    setNotificationObj({component:'',status:'',title:'',message:''})
  }
 //
 const usernameClasses = productIdHasErrors ? 'form-control invalid' : 'form-control';
 const passwordClasses = productNameHasError ? 'form-control invalid' : 'form-control';
 //

 return (
    <section className={classes.auth}>
      <h1>Login</h1> 
      <form onSubmit={submitHandler}>
        <div className={classes.control}>
          <label htmlFor='username'>Username</label>
          <input type='text' id='username' ref={usernameInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor='password'>Your Password</label>
          <input
            type='password'
            id='password'
            ref={passwordInputRef}
          />
          </div>
        
        <div className={classes.actions}>
            <button>Login</button>
        </div>
      </form>
    </section>
  );
};

export default LoginForm;
