import { useState } from 'react';
import Header from './components/Layout/Header';
import Meals from './components/Meals/Meals';
import Cart from './components/Cart/Cart';
import CartProvider from './store/CartProvider';
import BillForm from './components/Bill/BillForm';

function App() {
  const [cartIsShown, setCartIsShown] = useState(false);
  const [billForm,setOpetBillForm]=useState(false);

  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const hideCartHandler = () => {
    setCartIsShown(false);
  };

  const onOpenBillForm=()=>{
    setOpetBillForm(true);
  }
  const onCloseBillForm=()=>{
    setOpetBillForm(false);
  }

  return (
    <CartProvider>
      {cartIsShown && <Cart onClose={hideCartHandler} />}
      {/* {billForm && <BillForm onClose={onCloseBillForm}/>} */}
      <Header onShowCart={showCartHandler} />
      <main>
        <Meals />
        <BillForm onClose={onCloseBillForm}/>
      </main>
    </CartProvider>
  );
}

export default App;
