import {useSelector,useDispatch} from 'react-redux'
import Modal from '../UI/CartModal';
import CartItem from './CartItem';
import classes from './Cart.module.css';
import { cartActions } from '../../store/cartSlice';

const Cart = (props) => {
  const cart=useSelector((state)=>state.cart.items);
  const totalBillPrice=useSelector((state)=>state.cart.totalBillPrice)
  // const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
  const cartQuantity=useSelector(state=>state.totalAmount)
  const hasItems = cartQuantity>0;
  const dispatch=useDispatch();

  const cartItemRemoveHandler = (id) => {
    dispatch(cartActions.removeItemFromCart(id));
  };

  const cartItemAddHandler = (item) => {
    dispatch(cartActions.addItemToCart(item));
  };
  const cartItems = (
    <ul className={classes['cart-items']}>
      {cart.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.quantity}
          price={item.price}
          totalPrice={item.price * item.quantity}
          onRemove={cartItemRemoveHandler.bind(null, item.id)}
          onAdd={cartItemAddHandler.bind(null, item)}
        />

      ))}
    </ul>
  );


  return (
    <Modal onClose={props.onClose}>
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalBillPrice}</span>
      </div>
      <div className={classes.actions}>
        <button className={classes['button--alt']} onClick={props.onClose}>
          Close
        </button>
        {hasItems && <button className={classes.button}>Order</button>}
      </div>
    </Modal>
  );
};

export default Cart;
