const AvailableMeals = () => {

  const [products,setProducts]=useState([]);
  const getProducts=async ()=>{
    const data = await fetch(`http://localhost:7269/api/Product`);
    const product=await data.json();
    setProducts(product);
   }

   useEffect(() => {          //svaki put kad se redneruje cuisine komponenta redneruje se i ovaj useEffect i plus se renderuje opet svaki put 
    getProducts();  //kada se promeni url ,to jest params.type sto znaci kad odes na neku drugu stranicu on ti fechuje nove podatke
    console.log(products);  //po tom odredjenom params-u
   },[products]);




  const mealsList = products.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
