import { useSelector, useDispatch } from "react-redux";
import Modal from "../UI/Modal";
import CartItem from "./CartItem";
import classes from "./Cart.module.css";
import { billActions } from "../../store/billSlice";
import { uiActions } from "../../store/uiSlice";
import { useState, useRef } from "react";

const Cart = (props) => {
  const cart = useSelector((state) => state.bill.items);
  const totalBillPrice = useSelector((state) => state.bill.totalBillPrice);
  const totalNum = useSelector((state) => state.bill.totalAmount);
  const hasItems = totalNum > 0;
  const billNumber = useSelector((state) => state.bill.billNumber);
  const dispatch = useDispatch();
  const [printed, setPrinted] = useState(false);
  const [billNumberChange, setBillNumberChange] = useState(false);
  const billInputRef = useRef();

  const cartItemRemoveHandler = (id) => {
    dispatch(billActions.removeItemFromCart(id));
  };

  const cartItemAddHandler = (item) => {
    dispatch(billActions.addItemToCart(item));
  };

  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cart.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.quantity}
          price={item.price}
          totalPrice={item.price * item.quantity}
          onRemove={cartItemRemoveHandler.bind(null, item.id)}
          onAdd={cartItemAddHandler.bind(null, item)}
        />
      ))}
    </ul>
  );
  const onCloseAfterPrint = () => {
    setPrinted((prevState) => !prevState);
    props.onClose();
    dispatch(billActions.resetToInitial());
    dispatch(uiActions.showBillButton());
  };

  const onPrintHandler = () => {
    setPrinted((prevState) => !prevState);
    console.log("onprinthandler");
  };

  const onChangeBillNumberHandler = () => {
    setBillNumberChange((prevState) => !prevState);
  };

  return (
    <Modal onClose={props.onClose}>
      {!printed && !billNumberChange && (
        <div>
          <div className={classes.head}>
            <span>Bill Number : </span>
            <span className={classes.sp}>{billNumber}</span>
            <button
              id="billButton"
              className={classes.billButton}
              onClick={onChangeBillNumberHandler}
            >
              Change BN
            </button>
          </div>
          {cartItems}
          <div className={classes.total}>
            <span>Total Price</span>
            <span>{totalBillPrice}din</span>
          </div>
          <div className={classes.actions}>
            <button
              id="closeButton"
              className={classes["button--alt"]}
              onClick={props.onClose}
            >
              Close
            </button>
            {hasItems && (
              <button
                id="billButton"
                className={classes.billButton}
                onClick={onPrintHandler}
              >
                Print
              </button>
            )}
          </div>
        </div>
      )}
      {printed && (
        <div>
          <h1>Bill is successfuly printed !</h1>
          <button
            id="closeButton"
            className={classes.billButton}
            onClick={onCloseAfterPrint}
          >
            Close
          </button>
        </div>
      )}
      {billNumberChange && (
        <div className={classes.billChange}>
          <form className={classes.billChangeForm}>
            <div>
              <label htmlFor="billNumber">Bill number: </label>
              <input id="billNumber" type="text" ref={billInputRef}></input>
            </div>

            <button className={classes.billButton}>Change</button>
          </form>
        </div>
      )}
    </Modal>
  );
};

export default Cart;
