import { useState, useRef, useContext, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import AuthContext from '../../store/auth-context';
import classes from './AuthForm.module.css';
import { billActions } from '../../store/billSlice';
import {useSelector,useDispatch} from 'react-redux';
import { uiActions } from '../../store/uiSlice';

const AuthForm = () => {
  const history = useHistory();
  const usernameInputRef = useRef();
  const passwordInputRef = useRef();
  const [token,setToken]=useState('');
  const dispatch=useDispatch()
  
  const authCtx = useContext(AuthContext);
  const [userObj,setUserObj]=useState({})

  async function GetUsersId(username){
    const response = await fetch(`https://localhost:7269/api/User/GetUsersId/${username}`);
    const data = await response.json();
    console.log(response)
    console.log(data);
    dispatch(billActions.setUserId(data))  
  }
  async function GetUserByCredentials(userObj){
    const user=JSON.stringify(userObj);
    const response = await fetch('https://localhost:7269/api/User/GetUserByCredentials', {
      method: 'POST',
      body: user,
      headers: {
        'Content-Type': 'application/json'
      }
    });
    const data = await response.json();
    setUserObj(data);

  }

  async function Login(userCredsObj) {
    //setNotificationObj({component:'ProductForm',status:'pending',title:'Sending',message:'Sending...'})
    const credentials=JSON.stringify(userCredsObj);
    console.log(credentials)
    const response = await fetch('https://localhost:7269/api/User/login', {
      method: 'POST',
      body: credentials,
      headers: {
        'Content-Type': 'application/json',
        'Accept':'application/json'
      }
    });
    console.log(response)
    const data=await response.json()
    setToken(data);
    console.log(data)
    if (!response.ok) {
      console.log(data)
      throw new Error('error');
    }
    const expirationTime = new Date(
            new Date().setHours(new Date().getHours() + 8)
          )
    authCtx.login(data, expirationTime.toISOString());
    GetUsersId(userCredsObj.username);



    const getUserResponse = await fetch('https://localhost:7269/api/User/GetUserByCredentials', {
      method: 'POST',
      body: credentials,
      headers: {
        'Content-Type': 'application/json'
      }
    });
    const getUserData = await getUserResponse.json();

    if(getUserData.role === 'admin'){
      history.replace('/admin')
      dispatch(uiActions.isOnAdminsPage())
    }
    else if(getUserData.role === 'user'){
      history.replace('/homepage')
    }                                    
    //history.replace('/homepage');
    
   // dispatch(productActions.addProduct(productObj))
   // setNotificationObj({component:'ProductForm',status:'success',title:'Success!',message:'Successfuly created product'})
  }

  

  // const switchAuthModeHandler = () => {
  //   setIsLogin((prevState) => !prevState);
  // };
 
  const submitHandler = (event) => {
    event.preventDefault();
    
    const enteredUsername = usernameInputRef.current.value;
    const enteredPassword = passwordInputRef.current.value;
    
    // optional: Add validation
 
    // setIsLoading(true);
      const credentials={username:enteredUsername,password:enteredPassword}
      Login(credentials);
}
 return (
    <section className={classes.auth}>
      <h1>Login</h1> 
      <form onSubmit={submitHandler}>
        <div className={classes.control}>
          <label htmlFor='username'>Username</label>
          <input type='text' id='username' ref={usernameInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor='password'>Your Password</label>
          <input
            type='password'
            id='password'
            ref={passwordInputRef}
          />
          </div>
        
        <div className={classes.actions}>
            <button>Login</button>
        </div>
      </form>
    </section>
  );
};

export default AuthForm;
