import { useState,useEffect } from 'react';

import Header from './components/Layout/Header';
import Meals from './components/Meals/Meals';
import Cart from './components/Cart/Cart';
import CartProvider from './store/CartProvider';

function App() {
  const [cartIsShown, setCartIsShown] = useState(false);

  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const hideCartHandler = () => {
    setCartIsShown(false);
  };


  const [products,setProducts]=useState([]);
  const getProducts=async ()=>{
    const data = await fetch(`https://localhost:7269/api/Product`);
    const product=await data.json();
    setProducts(product);
   }

   useEffect(() => {          //svaki put kad se redneruje cuisine komponenta redneruje se i ovaj useEffect i plus se renderuje opet svaki put 
    getProducts();  //kada se promeni url ,to jest params.type sto znaci kad odes na neku drugu stranicu on ti fechuje nove podatke
    console.log(products);  //po tom odredjenom params-u
   },[products]);


  return (
    <CartProvider>
      {cartIsShown && <Cart onClose={hideCartHandler} />}
      <Header onShowCart={showCartHandler} />
      <main>
        <Meals />
      </main>
      <div>
        <p>cao</p>
      </div>
    </CartProvider>
  );
}

export default App;
