import { Fragment } from 'react';
import {useSelector} from 'react-redux'
import HeaderCartButton from './HeaderCartButton';
import classes from './Header.module.css';
import { useHistory } from 'react-router-dom';

const Header = (props) => {
  const showBill=useSelector(state=>state.ui.createdBill)
  const history=useHistory();
  const onOpenBillsList=()=>{
   history.push()
  }

  return (
    <Fragment>
      <header className={classes.header}>
        <h1>CASH-REGISTER</h1>
        <button className={classes.btn} onClick={onOpenBillsList}>Bills List</button>
        <button className={classes.btn} onClick={props.onShowProductForm}>Add new Product</button>
        {!showBill && <button className={classes.btn} onClick={props.onShowCreateBill}>Create Bill</button>}
        {showBill && <HeaderCartButton onClick={props.onShowCart}/>}
      </header>
    </Fragment>
  );
};
export default Header;
