import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import { useState, useEffect, useCallback } from "react";
import Notification from "../UI/Notification";
import {useSelector,useDispatch} from 'react-redux'
import { uiActions } from "../../store/uiSlice";
import { productActions } from "../../store/productSlice";


function wait(ms){
  var start = new Date().getTime();
  var end = start;
  while(end < start + ms) {
    end = new Date().getTime();
 }
}

const AvailableMeals = () => {
  const [products, setProducts] = useState([]);
  const dispatch=useDispatch();
  const notification=useSelector(state=>state.ui.notification)
  const productsDb=useSelector(state=>state.products.items)
//
//
//
  const addProductsToBill=useCallback(async (bp) => {
    const billProduct=JSON.stringify({bill_number:bp.billNumber,product_id:bp.product_id,product_quantity:bp.product_quantity,products_cost:bp.products_cost});
   await fetch('https://localhost:7269/api/BillProduct/AddProductToBillProduct', {
      method: 'POST',
      body: billProduct,
      headers: {
        'Content-Type': 'application/json'
      }
    });
    console.log(billProduct)
  },[]);

  const deleteProduct=useCallback(async (product)=> {
    const response = await fetch(`https://localhost:7269/api/Product/DeleteProduct/${product}`, {
      method: 'DELETE',

    });
    const data = await response.json();
    console.log(data);

  },[]);

  const AddProduct=useCallback(async (productObj)=> {
    dispatch(
      uiActions.showNotification({
        component:'ProductForm',
        status: 'pending',
        title: 'Sending...',
        message: 'Adding product!',
      })
    );
    const product=JSON.stringify(productObj);
    const response = await fetch('https://localhost:7269/api/Product/CreateProducts', {
      method: 'POST',
      body: product,
      headers: {
        'Content-Type': 'application/json'
      }
    });
    const data=await response.json()
    console.log(data)
    if (!response.ok) {
      throw new Error(data.errorMessage);
    }
    wait(500)
    dispatch(uiActions.setNotificationToNull())
  },[]);
//
//
//
  const fetchProducts = useCallback(async () => {
    dispatch(
      uiActions.showNotification({
        component:'AvailableMeals',
        status: 'pending',
        title: 'Fetching...',
        message: 'Fetching Products!',
      })
    );
    const response = await fetch("https://localhost:7269/api/Product");
    const responseData=await response.json()

    if (!response.ok) {
      throw new Error();
    }


    const loadedMeals = [];

    for (const key in responseData) {
      loadedMeals.push({
        id: responseData[key].product_id,
        name: responseData[key].name,
        price: responseData[key].cost,
      });
    }
    wait(500)
    setProducts(loadedMeals);
    dispatch(productActions.replaceProducts(loadedMeals))
    dispatch(uiActions.setNotificationToNull())
  }, []);

  useEffect(() => {
    fetchProducts().catch((error) => {
      dispatch(
        uiActions.showNotification({
          component:'AvailableMeals',
          status: 'error',
          title: 'Error!',
          message: 'Connection error or product db is empty!',
        })
      );
    });
    console.log('avail')
  }, [fetchProducts]);


  const mealsList = products.map((product) => (
    <MealItem
      key={product.id}
      id={product.id}
      name={product.name}
      price={product.price}
      onAddProduct={addProductsToBill}
      onDeleteProduct={deleteProduct}
    />
  ));

  //
  //
  //
  //
  //
  //
  //
  //
  // async function addProductsToBill(bp) {
  //   const billProduct=JSON.stringify({bill_number:bp.billNumber,product_id:bp.product_id,product_quantity:bp.product_quantity,products_cost:bp.products_cost});
  //  await fetch('https://localhost:7269/api/BillProduct/AddProductToBillProduct', {
  //     method: 'POST',
  //     body: billProduct,
  //     headers: {
  //       'Content-Type': 'application/json'
  //     }
  //   });
  //   console.log(billProduct)
  // }

  // const addProductsToBill=useCallback(async (bp) => {
  //   const billProduct=JSON.stringify({bill_number:bp.billNumber,product_id:bp.product_id,product_quantity:bp.product_quantity,products_cost:bp.products_cost});
  //  await fetch('https://localhost:7269/api/BillProduct/AddProductToBillProduct', {
  //     method: 'POST',
  //     body: billProduct,
  //     headers: {
  //       'Content-Type': 'application/json'
  //     }
  //   });
  //   console.log(billProduct)
  // },[]);

  // async function deleteProduct(product) {

  //   const response = await fetch(`https://localhost:7269/api/Product/DeleteProduct/${product}`, {
  //     method: 'DELETE',

  //   });
  //   const data = await response.json();
  //   console.log(data);

  // }

  //
  //
  //
  //
  //
  //
  //
  //
  return (
    <section className={classes.meals}>
      <Card>

        {notification && notification.component==='AvailableMeals' && 
          <Notification
            status={notification.status}
            title={notification.title}
            message={notification.message}
          />
        }
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
