import React, { useRef, useState } from "react";
import Modal from "../UI/BillModal";
import classes from './BillForm.module.css'
import {useDispatch} from 'react-redux'
import { cartActions } from "../../store/cartSlice";

function BillForm(props) {

  const [enteredBillNumber,setEnteredBillNumber]=useState('');

  const dispatch=useDispatch();
  const billInputRef=useRef();
  const onSubmitHandler=(event)=>{
    dispatch(cartActions.createBill(billInputRef.current.value))
    console.log(billInputRef.current.value)
  }

  return (
    <Modal>
      <form onSubmit={onSubmitHandler}>
        <label>Bill number</label>
        <input type="text" ref={billInputRef}></input>
      </form>
      <div className={classes.actions}>
        <button className={classes['button--alt']} onClick={props.onClose}>
          Close
        </button>
        <button className={classes.button} onClick={props.onCreate}>Create</button>
      </div>
    </Modal>
  );
}

export default BillForm;
