import { useSelector, useDispatch } from "react-redux";
import Modal from "../UI/Modal";
import CartItem from "./CartItem";
import classes from "./Cart.module.css";
import { billActions } from "../../store/billSlice";
import { uiActions } from "../../store/uiSlice";
import {useState,useRef } from 'react'

const Cart = (props) => {
  const cart = useSelector((state) => state.bill.items);
  const totalBillPrice = useSelector((state) => state.bill.totalBillPrice);
  const totalNum = useSelector((state) => state.bill.totalAmount);
  const hasItems = totalNum > 0;
  const billNumber = useSelector((state) => state.bill.billNumber);
  const dispatch = useDispatch();
  const [printed,setPrinted]=useState(false);
  //changing bill number, and adding credit cardd
  const [billNumberChange,setBillNumberChange]=useState(false);
  const [creditCardAdd,setCreditCardAdd]=useState(false);
  const billInputRef=useRef();
  const creditCardInputRef=useRef();
  //update bill
  const [bp, setbp] = useState({});
  const [ps, setps] = useState([]);

  const cartItemRemoveHandler = (id) => {
    dispatch(billActions.removeItemFromCart(id));
  };

  const cartItemAddHandler = (item) => {
    dispatch(billActions.addItemToCart(item));
  };


  async function GetBillProducts(billNumber,newBillNumber) {
    const response=await fetch(`https://localhost:7269/api/Bill/GetBillById${billNumber}`);

    const data=await response.json();

          setbp(data);
          console.log(data)

    console.log(data)

    const bill=JSON.stringify({bill_number:newBillNumber,total_cost: data.total_cost,credit_card:data.credit_card,bill_Products:data.bill_Products});


    await fetch(
      'https://localhost:7269/api/Bill/UpdateBill',
      {
        method: 'PUT',
        body: bill,
        'Content-Type':'application-json'
      }
    );
  }
  // async function ChangeBillNumber(bill){

  //   const response = await fetch(
  //     'https://localhost:7269/api/Bill/UpdateBill',
  //     {
  //       method: 'PUT',
  //       body: JSON.stringify(bill),
  //     }
  //   );

  //   if (!response.ok) {
  //     throw new Error('Sending cart data failed.');
  //   }
  // }



  const changeBillNumber=(event)=>{
    event.preventDefault()
    GetBillProducts(billNumber);
    // console.log(bp)
    // ChangeBillNumber(bp)
    // const findedBill = bills.find((bill) => bill.id === billNum);
    // console.log(findedBill)






  }


  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cart.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.quantity}
          price={item.price}
          totalPrice={item.price * item.quantity}
          onRemove={cartItemRemoveHandler.bind(null, item.id)}
          onAdd={cartItemAddHandler.bind(null, item)}
        />
      ))}
    </ul>
  );
  const onCloseAfterPrint=()=>{
    setPrinted(prevState=>!prevState)
    props.onClose()
    dispatch(billActions.resetToInitial())
    dispatch(uiActions.showBillButton())
  }


   const onPrintHandler=()=>{
      setPrinted(prevState=>!prevState)
    console.log('onprinthandler')
   }

   const onChangeBillNumberHandler=()=>{
     setBillNumberChange(prevState=>!prevState);

   }
   const onAddCreditCardHandler=()=>{
    setCreditCardAdd(prevState=>!prevState)
   }




  return (
    <Modal onClose={props.onClose}>
      {!printed && !billNumberChange && !creditCardAdd &&
      <div>
       <div className={classes.head}>
        <span>Bill Number : </span>
        <span className={classes.sp}>{billNumber}</span>
        <button id="billButton" className={classes.billButton} onClick={onChangeBillNumberHandler}>Change BN</button>
        <button id="billButton" className={classes.billButton} onClick={onAddCreditCardHandler}>Add Credit Card</button>
      </div>
      {cartItems}
      <div className={classes.total}>
        <span>Total Price</span>
        <span>{totalBillPrice}din</span>
      </div>
      <div className={classes.actions}>
        <button id="closeButton" className={classes["button--alt"]} onClick={props.onClose}>
          Close
        </button>
        {hasItems && <button id="billButton" className={classes.billButton} onClick={onPrintHandler}>Print</button>}
      </div> 
      </div> }
      {printed && 
      <div>
        <h1>Bill is successfuly printed !</h1>
        <button id="closeButton" className={classes.billButton} onClick={onCloseAfterPrint}>Close</button>
      </div>
      }
      {billNumberChange && !creditCardAdd && 
      <div className={classes.billChange}>
        <form className={classes.billChangeForm}>
          <div>
          <label htmlFor="billNumber">Bill number: </label>
          <input id="billNumber" type="text" ref={billInputRef}></input>
          </div>
          <div>
          <button className={classes.billButton} onClick={onChangeBillNumberHandler} >Back</button>
          <button className={classes.billButton} onClick={changeBillNumber}>Change</button>
          </div>

        </form>
      </div>
      }
      {creditCardAdd && 
      <div className={classes.billChange}>
        <form className={classes.billChangeForm}>
          <div>
          <label htmlFor="creditCard">Credit card: </label>
          <input id="creditCard" type="text" ref={billInputRef}></input>
          </div>
          <div>
          <button className={classes.billButton} onClick={onAddCreditCardHandler} >Back</button>
          <button className={classes.billButton} ref={creditCardInputRef}>Add</button>
          </div>

        </form>
      </div>
      }
    </Modal>
  );
};

export default Cart;
