import { createSlice } from "@reduxjs/toolkit";


const userSlice = createSlice({
  name: "user",
  initialState: {
    isAdmin:false
  },
  reducers: {
    
    isAdmin(state){
      state.isAdmin=true;
    },
    backToInitial(state){
        state.isAdmin=false;
    }
  },
});

export const userActions = userSlice.actions;

export default userSlice;
