import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import { useState, useEffect, useCallback } from "react";
import Notification from "../UI/Notification";
import {useSelector,useDispatch} from 'react-redux'
import { uiActions } from "../../store/uiSlice";


function wait(ms){
  var start = new Date().getTime();
  var end = start;
  while(end < start + ms) {
    end = new Date().getTime();
 }
}


const AvailableMeals = () => {
  const [products, setProducts] = useState([]);
  const dispatch=useDispatch();
  const notification=useSelector(state=>state.ui.notification)


  const fetchProducts = async () => {
    dispatch(
      uiActions.showNotification({
        component:'AvailableMeals',
        status: 'pending',
        title: 'Fetching...',
        message: 'Fetching Products!',
      })
    );
    const response = await fetch("https://localhost:7269/api/Product");
    const responseData=await response.json()

    if (!response.ok) {
      throw new Error();
    }


    const loadedMeals = [];

    for (const key in responseData) {
      loadedMeals.push({
        id: responseData[key].product_id,
        name: responseData[key].name,
        price: responseData[key].cost,
      });
    }
    wait(500)
    setProducts(loadedMeals);
    dispatch(uiActions.setNotificationToNull())
  };

  useEffect(() => {
    fetchProducts().catch((error) => {
      dispatch(
        uiActions.showNotification({
          component:'AvailableMeals',
          status: 'error',
          title: 'Error!',
          message: 'Connection error or product db is empty!',
        })
      );
    });
    console.log('avail')
  }, [fetchProducts]);


  const mealsList = products.map((product) => (
    <MealItem
      key={product.id}
      id={product.id}
      name={product.name}
      price={product.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>

        {notification && notification.component==='AvailableMeals' && 
          <Notification
            status={notification.status}
            title={notification.title}
            message={notification.message}
          />
        }
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
